settings.custom_liquid:
{% if product.metafields.custom.unit and product.metafields.custom.unit != blank %}
  {% assign unit_raw = product.metafields.custom.unit | strip | downcase %}
  {% assign unit_clean = unit_raw | remove: " " %}
  {% assign quantity_str = unit_clean 
    | remove: "milliliter" 
    | remove: "ml" 
    | remove: "liter" 
    | remove: "l" 
    | remove: "kilogramm" 
    | remove: "kilogram" 
    | remove: "kg" 
    | remove: "gramm" 
    | remove: "gram" 
    | remove: "g"
    | remove: "stk"
    | remove: "st√ºck"
    | remove: "st" %}
  {% assign quantity = quantity_str | plus: 0 %}
  {% assign unit_type = unit_clean | remove: quantity_str %}
  {% assign standard_unit = "" %}
  {% assign converted_quantity = 0 %}

  {% if unit_type == "ml" or unit_type == "milliliter" %}
    {% assign converted_quantity = quantity | times: 0.001 %}
    {% assign standard_unit = "Liter" %}
  {% elsif unit_type == "l" or unit_type == "liter" %}
    {% assign converted_quantity = quantity %}
    {% assign standard_unit = "Liter" %}
  {% elsif unit_type == "g" or unit_type == "gram" or unit_type == "gramm" %}
    {% assign converted_quantity = quantity | times: 0.001 %}
    {% assign standard_unit = "Kilogramm" %}
  {% elsif unit_type == "kg" or unit_type == "kilogram" or unit_type == "kilogramm" %}
    {% assign converted_quantity = quantity %}
    {% assign standard_unit = "Kilogramm" %}
  {% elsif unit_type == "st" or unit_type == "stk" or unit_type == "st√ºck" %}
    {% if quantity == 0 %}
      {% assign quantity = 1 %}
    {% endif %}
    {% assign converted_quantity = quantity %}
    {% assign standard_unit = "St√ºck" %}
  {% endif %}

  {% if converted_quantity > 0 and standard_unit != "" %}
    {% assign variant_price = product.selected_or_first_available_variant.price | plus: 0 %}
    {% assign base_price = variant_price | divided_by: converted_quantity %}
    <div class="base-price" style="margin-top:10px;">
      {{ base_price | money }} inkl. MwSt. pro {{ standard_unit }}
    </div>
  {% endif %}
{% endif %}

{% assign show_warnings = false %}

{% if product.tags contains 'spedition' or product.tags contains 'ab18' or product.tags contains 'gefahrgut' or product.tags contains 'Klasse1' or product.tags contains 'Klasse2' %}
  {% assign show_warnings = true %}
{% endif %}

{% if show_warnings %}
  <div class="product-warnings" style="border: 1px solid #ddd; padding: 10px; margin: 10px 0; border-radius: 5px;">

    {% if product.tags contains 'spedition' %}
      <p style="margin: 0 0 5px 0; font-weight: bold; color: #333;">‚ö†Ô∏è Speditionsware</p>
    {% endif %}

    {% if product.tags contains 'ab18' %}
      <p style="margin: 0 0 5px 0; font-weight: bold; color: #333;">üîû Verkauf nur an Vollj√§hrige</p>
    {% endif %}

    {% if product.tags contains 'Klasse1' %}
      <p style="margin: 0 0 5px 0; font-weight: bold; color: #d63031;">‚ö†Ô∏è Gefahrgut Klasse1 - Versandzuschlag wird berechnet</p>
    {% elsif product.tags contains 'Klasse2' %}
      <p style="margin: 0 0 5px 0; font-weight: bold; color: #d63031;">‚ö†Ô∏è Gefahrgut Klasse2 - Versandzuschlag wird berechnet</p>
    {% elsif product.tags contains 'gefahrgut' %}
      <p style="margin: 0 0 5px 0; font-weight: bold; color: #d63031;">‚ö†Ô∏è Gefahrgut - Versandzuschlag wird berechnet</p>
    {% endif %}

  </div>
{% endif %}

{% comment %}Live Social Proof Counter - Nur f√ºr kaufbare Produkte{% endcomment %}

{% if product.available %}
{% assign names = "Sabine,Michael,Julia,Thomas,Anna,Peter,Laura,Stefan,Nina,Jan,Daria,Petra,Frank,Claudia,Markus,Sandra,James,Danil,Sarah,David,Emma,John,Lisa,Robert,Maria,William,Jennifer,Ahmet,Fatma,Mehmet,Ay≈üe,Mustafa,Zeynep,Cem,Selin,Burak,Elif,Alexander,Dasha,Anastasia,Dmitri,Svetlana,Igor,Fabio,Natasha,Alexei,Olga,Pavel,Irina,Sergei" | split: "," %}

{% assign day_seed = 'now' | date: '%j' | plus: 0 %}
{% assign hour_seed = 'now' | date: '%H' | plus: 0 %}
{% assign product_seed = product.id %}
{% assign combined_seed = day_seed | plus: product_seed | plus: hour_seed %}

{% comment %}Name basierend auf Produkt, Tag und Stunde{% endcomment %}
{% assign name_index = combined_seed | modulo: names.size %}
{% assign selected_name = names[name_index] %}

{% comment %}Bessere Startzahl-Berechnung: Jedes Produkt hat eine einzigartige Zahl zwischen 3-47{% endcomment %}
{% assign product_multiplier = product.id | times: 37 %}
{% assign day_factor = day_seed | times: 13 %}
{% assign base_number = product_multiplier | plus: day_factor | modulo: 45 | plus: 3 %}

<div class="social-proof-live" style="background-color: #f8f9fa; border-left: 4px solid #28a745; padding: 12px 16px; margin: 16px 0; border-radius: 4px;">
  <span style="color: #155724; font-weight: 500;">
    <span id="customer-name">{{ selected_name }}</span> und 
    <span id="live-counter">{{ base_number }}</span> weitere haben diesen Artikel heute schon gekauft
  </span>
  <span style="display: inline-block; width: 8px; height: 8px; background-color: #28a745; border-radius: 50%; margin-left: 8px; animation: pulse 2s infinite;" id="live-indicator"></span>
</div>

<style>
@keyframes pulse {
  0% { opacity: 1; }
  50% { opacity: 0.5; }
  100% { opacity: 1; }
}

.social-proof-live {
  position: relative;
  transition: all 0.3s ease;
}

.number-update {
  animation: numberPulse 0.5s ease;
}

@keyframes numberPulse {
  0% { transform: scale(1); }
  50% { transform: scale(1.1); color: #dc3545; }
  100% { transform: scale(1); }
}

.name-update {
  animation: fadeInOut 1s ease;
}

@keyframes fadeInOut {
  0% { opacity: 1; }
  50% { opacity: 0.3; }
  100% { opacity: 1; }
}
</style>

<script>
(function() {
  const productId = {{ product.id }};
  const storageKey = `socialProofCount_${productId}`;
  const nameStorageKey = `socialProofName_${productId}`;
  const lastNameChangeKey = `socialProofNameChange_${productId}`;
  const baseNumber = {{ base_number }};
  const defaultName = "{{ selected_name }}";

  // Internationale Namen-Arrays
  const names = [
    // Deutsche Namen
    "Sabine", "Michael", "Julia", "Thomas", "Anna", "Peter", "Laura", "Stefan", "Nina", "Jan", 
    "Petra", "Frank", "Claudia", "Markus", "Sandra", "Andreas", "Susanne", "Christian", "Birgit", "Wolfgang",

    // Englische Namen
    "James", "Sarah", "David", "Emma", "John", "Lisa", "Robert", "Maria", "William", "Jennifer",
    "Christopher", "Jessica", "Daniel", "Ashley", "Matthew", "Amanda", "Anthony", "Melissa", "Mark", "Deborah",

    // T√ºrkische Namen
    "Ahmet", "Fatma", "Mehmet", "Ay≈üe", "Mustafa", "Zeynep", "Cem", "Selin", "Burak", "Elif",
    "Emre", "Derya", "Hasan", "Sevgi", "ƒ∞brahim", "G√ºl", "Oƒüuz", "Nur", "Yusuf", "Esra",

    // Russische Namen
    "Alexander", "Anastasia", "Dmitri", "Svetlana", "Igor", "Natasha", "Alexei", "Olga", "Pavel", "Irina",
    "Sergei", "Elena", "Mikhail", "Yulia", "Andrei", "Tatyana", "Vladimir", "Galina", "Nikolai", "Lyudmila"
  ];

  // Elemente referenzieren
  const counterElement = document.getElementById('live-counter');
  const nameElement = document.getElementById('customer-name');

  // Gespeicherte Werte laden
  let currentCount = parseInt(localStorage.getItem(storageKey)) || baseNumber;
  let savedName = localStorage.getItem(nameStorageKey) || defaultName;
  let lastNameChange = parseInt(localStorage.getItem(lastNameChangeKey)) || Date.now();

  // Sicherstellen, dass die Zahl nie unter dem Basiswert liegt
  if (currentCount < baseNumber) {
    currentCount = baseNumber;
  }

  // Gespeicherte Werte sofort anzeigen
  counterElement.textContent = currentCount;
  nameElement.textContent = savedName;

  // Werte speichern
  localStorage.setItem(storageKey, currentCount.toString());
  localStorage.setItem(nameStorageKey, savedName);

  // Namen-Wechsel Funktion
  function changeRandomName() {
    const now = Date.now();
    // Namen alle 2-5 Minuten wechseln
    if (now - lastNameChange > (120000 + Math.random() * 180000)) {
      const randomIndex = Math.floor(Math.random() * names.length);
      const newName = names[randomIndex];

      if (newName !== savedName) {
        nameElement.classList.add('name-update');
        nameElement.textContent = newName;
        savedName = newName;
        lastNameChange = now;

        // Werte speichern
        localStorage.setItem(nameStorageKey, savedName);
        localStorage.setItem(lastNameChangeKey, lastNameChange.toString());

        // Animation zur√ºcksetzen
        setTimeout(() => {
          nameElement.classList.remove('name-update');
        }, 1000);
      }
    }
  }

  // Intervalle f√ºr Counter-Updates
  const intervals = [
    8000,   // 8 Sekunden
    12000,  // 12 Sekunden
    18000,  // 18 Sekunden
    25000,  // 25 Sekunden
    32000,  // 32 Sekunden
    40000,  // 40 Sekunden
    50000,  // 50 Sekunden
    65000   // 65 Sekunden
  ];

  // Pseudo-zuf√§llige Intervall-Auswahl
  function getRandomInterval() {
    const now = Date.now();
    const seed = (now + productId + currentCount) % intervals.length;
    return intervals[seed];
  }

  // Counter erh√∂hen
  function incrementCounter() {
    // 70% Wahrscheinlichkeit f√ºr +1, 30% f√ºr +2 bis +3
    const increment = Math.random() < 0.7 ? 1 : Math.floor(Math.random() * 2) + 2;
    currentCount += increment;

    // Sofort im localStorage speichern
    localStorage.setItem(storageKey, currentCount.toString());

    // Animation und Anzeige
    counterElement.classList.add('number-update');
    counterElement.textContent = currentCount;

    // Namen eventuell wechseln
    changeRandomName();

    // Animation zur√ºcksetzen
    setTimeout(() => {
      counterElement.classList.remove('number-update');
    }, 500);

    // N√§chste Aktualisierung planen
    scheduleNextUpdate();
  }

  // N√§chste Aktualisierung planen
  function scheduleNextUpdate() {
    const nextInterval = getRandomInterval();
    setTimeout(incrementCounter, nextInterval);
  }

  // Sichtbarkeit √ºberwachen
  let isVisible = !document.hidden;

  document.addEventListener('visibilitychange', function() {
    isVisible = !document.hidden;
  });

  // Modifizierte increment-Funktion mit Sichtbarkeitspr√ºfung
  const originalIncrement = incrementCounter;
  incrementCounter = function() {
    if (isVisible) {
      originalIncrement();
    } else {
      // Auch wenn nicht sichtbar, n√§chsten Update planen
      scheduleNextUpdate();
    }
  };

  // Ersten Update nach kurzer Zeit starten (5-15 Sekunden)
  const initialDelay = Math.floor(Math.random() * 10000) + 5000;
  setTimeout(() => {
    if (isVisible) {
      incrementCounter();
    }
  }, initialDelay);

})();
</script>

{% endif %}
