settings.custom_liquid:
{% if product.metafields.custom.unit and product.metafields.custom.unit != blank %}
  {% assign unit_raw = product.metafields.custom.unit | strip | downcase %}
  {% assign unit_clean = unit_raw | remove: " " %}
  {% assign quantity_str = unit_clean 
    | remove: "milliliter" 
    | remove: "ml" 
    | remove: "liter" 
    | remove: "l" 
    | remove: "kilogramm" 
    | remove: "kilogram" 
    | remove: "kg" 
    | remove: "gramm" 
    | remove: "gram" 
    | remove: "g"
    | remove: "stk"
    | remove: "stück"
    | remove: "st" %}
  {% assign quantity = quantity_str | plus: 0 %}
  {% assign unit_type = unit_clean | remove: quantity_str %}
  {% assign standard_unit = "" %}
  {% assign converted_quantity = 0 %}

  {% if unit_type == "ml" or unit_type == "milliliter" %}
    {% assign converted_quantity = quantity | times: 0.001 %}
    {% assign standard_unit = "Liter" %}
  {% elsif unit_type == "l" or unit_type == "liter" %}
    {% assign converted_quantity = quantity %}
    {% assign standard_unit = "Liter" %}
  {% elsif unit_type == "g" or unit_type == "gram" or unit_type == "gramm" %}
    {% assign converted_quantity = quantity | times: 0.001 %}
    {% assign standard_unit = "Kilogramm" %}
  {% elsif unit_type == "kg" or unit_type == "kilogram" or unit_type == "kilogramm" %}
    {% assign converted_quantity = quantity %}
    {% assign standard_unit = "Kilogramm" %}
  {% elsif unit_type == "st" or unit_type == "stk" or unit_type == "stück" %}
    {% if quantity == 0 %}
      {% assign quantity = 1 %}
    {% endif %}
    {% assign converted_quantity = quantity %}
    {% assign standard_unit = "Stück" %}
  {% endif %}

  {% if converted_quantity > 0 and standard_unit != "" %}
    {% assign variant_price = product.selected_or_first_available_variant.price | plus: 0 %}
    {% assign base_price = variant_price | divided_by: converted_quantity %}
    <div class="base-price" style="margin-top:10px;">
      {{ base_price | money }} inkl. MwSt. pro {{ standard_unit }}
    </div>
  {% endif %}
{% endif %}
